import React ,{useState , useEffect}from "react";
import DateAndTime from "./DateAndTime";

const Weather=()=>{
    const [city,setCity]= useState({
        weather:[{main:"Clear"}]
    });
    const [search,setSearch]= useState("Arunachal Pradesh");
    const [icon , setIcon] = useState("");

    useEffect(()=>{
        const fetchApi= async ()=>{
            const url=`http://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=bd8184da268988bea575aff8abcbdf3b`
            const response = await fetch(url);
            const resjson= await response.json();
            setCity(resjson);
            console.log(resjson);
            
            let weatherMain= city.clouds.all;
             
    let weatherIcon={
        Thunderstorm:"wi wi-thunderstorm",
        Drizzle:"wi wi-sleet",
        Rain:"wi wi-storm-showers",
        Snow:"wi wi-snow",
        Atmosphere:"wi wi-fog",
        Clear:"wi wi-day-sunny",
        Clouds:"wi wi-day-fog"
      }

      if(weatherMain > 0 && weatherMain < 15){
          setIcon(weatherIcon.Clear);
      } else if(weatherMain > 20 && weatherMain < 80){
        setIcon(weatherIcon.Clouds);
     } else if(weatherMain > 85 && weatherMain < 110){
        setIcon(weatherIcon.Rain);
    } else if(weatherMain > 120 && weatherMain < 150){
        setIcon(weatherIcon.Snow);
    } 

    
         
        };
        fetchApi();
    },[search])

    

   
    
    return (
        <>
        <div className="container">
            <div className="box">
            <div className="inputData">
                <input type="search" 
                className="inputField"
                placeholder="search any city..."
                onChange={(e)=>{
                 setSearch(e.target.value);
                }
                }
                value={search}    
                />
            </div>

            {!city.name ? (
            <p className="error">No result found</p>
            ) : (
                <>
            <h5 className="head">
                <i className={icon}/>
               
            </h5>
            <div className="main">
                <h2 className="location"><i className="fas fa-location-arrow"></i> {search}</h2>
                <h1 className="temp">{city.main.temp}°Cel</h1>
                <h3 className="min_max">Feels Like : {city.main.feels_like}°Cel </h3>
                <h3 className="time"><DateAndTime/></h3>
            </div>
            <div className="wave one"></div>
            <div className="wave two"></div>
            <div className="wave three"></div>
            </>
            )}
           
            </div>
        </div>
        </>
    );
};

export default Weather;
