import React ,{useState}from "react";

const Weather=()=>{
    const [city,setCity]= useState("");
    return (
        <>
        <div className="container">
            <div className="box">
            <div className="inputData">
                <input type="search" 
                className="inputField"
                placeholder="search any city..."
                onChange={(e)=>{
                 setCity(e.target.value);
                }}    
                />
            </div>
            <div className="head">
                <i className="fas fa-sun" style={{color:"orange"}}></i>
                <i className="fas fa-cloud" style={{color:"white"}}></i>
            </div>
            <div className="main">
                <h2 className="location"><i className="fas fa-location-arrow"></i> {city}</h2>
                <h1 className="temp">5.25°Cel</h1>
                <h3 className="min_max">Min : 5.25°Cel | Max : 5.25°Cel </h3>
            </div>
            <div className="wave one"></div>
            <div className="wave two"></div>
            <div className="wave three"></div>
            </div>
        </div>
        </>
    );
};

export default Weather;
