import React ,{useState , useEffect}from "react";

const Weather=()=>{
    const [city,setCity]= useState(null);
    const [search,setSearch]= useState("Arunachal Pradesh");
    const [icon , setIcon] = useState(null);

    useEffect(()=>{
        const fetchApi= async ()=>{
            const url=`http://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=bd8184da268988bea575aff8abcbdf3b`
            const response = await fetch(url);
            const resjson= await response.json();
            
            let weatherMain= resjson.weather[0].main;
             
            let weatherIcon={
                Thunderstorm:"wi wi-thunderstorm",
                Drizzle:"wi wi-sleet",
                Rain:"wi wi-storm-showers",
                Snow:"wi wi-snow",
                Atmosphere:"wi wi-fog",
                Clear:"wi wi-day-sunny",
                Clouds:"wi wi-day-fog"
              }

              if(weatherMain === "Thunderstorm"){
                  setIcon(weatherIcon.Thunderstorm);
              } else if(weatherMain === "Drizzle"){
                setIcon(weatherIcon.Drizzle);
             } else if(weatherMain === "Rain"){
                setIcon(weatherIcon.Rain);
            } else if(weatherMain === "Snow"){
                setIcon(weatherIcon.Snow);
            } else if(weatherMain === "Atmosphere"){
                setIcon(weatherIcon.Atmosphere);
            } else if(weatherMain === "Clear"){
                setIcon(weatherIcon.Clear);
            } else if(weatherMain === "Clouds"){
                setIcon(weatherIcon.Clouds);
            }

            setCity(resjson.main);

        };
        fetchApi();
    },[search])


    let currentTime= new Date();
    var weekday = new Array(7);
     weekday[0] = "Sunday";
    weekday[1] = "Monday"; 
     weekday[2] = "Tuesday";
    weekday[3] = "Wednesday";
    weekday[4] = "Thursday";
   weekday[5] = "Friday";
    weekday[6] = "Saturday";
    
    let day=weekday[currentTime.getDay()];

    let months=[
        "Jan",
        "Feb",
        "Mar",
        "Apr",
        "May",
        "Jun",
        "Jul",
        "Aug",
        "Sep",
        "Oct",
        "Nov",
        "Dec"
    ];

    let now = new Date();
    let month = months[now.getMonth()];
    let date=now.getDate();

    let time=new Date().toLocaleTimeString();

    const [ctime , setCtime] = useState(time);

    setInterval(() => {
        time=new Date().toLocaleTimeString();
        setCtime(time); 
    }, 1000);



    


    
    return (
        <>
        <div className="container">
            <div className="box">
            <div className="inputData">
                <input type="search" 
                className="inputField"
                placeholder="search any city..."
                onChange={(e)=>{
                 setSearch(e.target.value);
                }
                }
                value={search}    
                />
            </div>

            {!city ? (
            <p className="error">No result found</p>
            ) : (
                <>
            <h5 className="head">
                <i className={icon}/>
               
            </h5>
            <div className="main">
                <h2 className="location"><i className="fas fa-location-arrow"></i> {search}</h2>
                <h1 className="temp">{city.temp}°Cel</h1>
                <h3 className="min_max">Feels Like : {city.feels_like}°Cel </h3>
                <h3 className="time">{day} | {month} {date} | {ctime}</h3>
            </div>
            <div className="wave one"></div>
            <div className="wave two"></div>
            <div className="wave three"></div>
            </>
            )}
           
            </div>
        </div>
        </>
    );
};

export default Weather;
