import React ,{useState , useEffect}from "react";

const Weather=()=>{
    const [city,setCity]= useState("");
    const [search,setSearch]= useState("Pune");

    useEffect(()=>{
        const fetchApi= async ()=>{
            const url=`http://api.openweathermap.org/data/2.5/weather?q=${search}&appid=bd8184da268988bea575aff8abcbdf3b`
            const response = await fetch(url);
            const resjson= await response.json();
            setCity(resjson.main);
        }
        fetchApi();
    },[search])
    return (
        <>
        <div className="container">
            <div className="box">
            <div className="inputData">
                <input type="search" 
                className="inputField"
                placeholder="search any city..."
                onChange={(e)=>{
                 setSearch(e.target.value);
                }}    
                />
            </div>

            {!city ? (
            <p className="error">No result found</p>
            ) : (
                <>
            <div className="head">
                <i className="fas fa-sun" style={{color:"orange"}}></i>
                <i className="fas fa-cloud" style={{color:"white"}}></i>
            </div>
            <div className="main">
                <h2 className="location"><i className="fas fa-location-arrow"></i> {search}</h2>
                <h1 className="temp">{city.main.temp}</h1>
                <h3 className="min_max">Min : 5.25°Cel | Max : 5.25°Cel </h3>
            </div>
            </>
            )}
           
            <div className="wave one"></div>
            <div className="wave two"></div>
            <div className="wave three"></div>
            </div>
        </div>
        </>
    );
};

export default Weather;
