import React ,{useState , useEffect}from "react";

const Weather=()=>{
    const [city,setCity]= useState(null);
    const [search,setSearch]= useState("Pune");

    useEffect(()=>{
        const fetchApi= async ()=>{
            const url=`http://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=bd8184da268988bea575aff8abcbdf3b`
            const response = await fetch(url);
            const resjson= await response.json();
            console.log(resjson);
            setCity(resjson.main);
        };
        fetchApi();
    },[search])

    let time=new Date().toLocaleString();

    const [ctime , setCtime] = useState(time);


    setInterval(() => {
        time=new Date().toLocaleString();
        setCtime(time); 
    }, 1000);
    return (
        <>
        <div className="container">
            <div className="box">
            <div className="inputData">
                <input type="search" 
                className="inputField"
                placeholder="search any city..."
                onChange={(e)=>{
                 setSearch(e.target.value);
                }
                }
                value={search}    
                />
            </div>

            {!city ? (
            <p className="error">No result found</p>
            ) : (
                <>
            <div className="head">
                <i className="wi wi-day-sunny"></i>
               
            </div>
            <div className="main">
                <h2 className="location"><i className="fas fa-location-arrow"></i> {search}</h2>
                <h1 className="temp">{city.temp}°Cel</h1>
                <h3 className="min_max">Feels Like : {city.feels_like}°Cel </h3>
                <h3 className="time">{ctime}</h3>
            </div>
            <div className="wave one"></div>
            <div className="wave two"></div>
            <div className="wave three"></div>
            </>
            )}
           
            </div>
        </div>
        </>
    );
};

export default Weather;
